@Component({
  selector: 'app-navigation',
  templateUrl: './navigation.component.html',
  styleUrls: ['./navigation.component.css']
})
export class NavigationComponent {

}
<div class="nav-logo"></div>
<div class="nav-container">
    <ul>
        <li><a class="nav-btn" routerLink="/home">Home</a></li>
        <li><a class="nav-btn" routerLink="/catalog">Browse</a></li>
        <li><a class="nav-btn" routerLink="/upload">Upload</a></li>
        <li><a class="nav-btn" routerLink="/login">Login</a></li>
        <li><a class="nav-btn" routerLink="/register">Register</a></li>
        <li><a class="nav-btn" routerLink="/about">About</a></li>
    </ul>
</div>
